import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { AjouterAction,SuprmierAction } from './Redux/InitialAction'
export default function Affihcer() {
  const [valu1,setvalu1]=useState()
  const [valu2,setvalu2]=useState()
  const dispatch= useDispatch()
    const content=useSelector((data)=>data.val)

    const handlClick=()=>{
      dispatch(
        AjouterAction({
        id:Math.floor(Math.random()*1000),
        elm1:valu1,
        elm2:valu2
      }))
      setvalu1("")
    }

    function handlDelete(iid){
      dispatch(SuprmierAction(iid))
    }

  return (
    <div className='container col-8 shadow-lg m-2 p-2 w'>
       <div className='d-flex mb-3'>
       <input style={{"width":"40%"}} type='text' className=' me-3 form-control' value={valu1} onChange={(e)=>setvalu1(e.target.value)}/>
       <input style={{"width":"40%"}} type='text' className=' me-3 form-control' value={valu2} onChange={(e)=>setvalu2(e.target.value)}/>
        <button className='w-20 btn btn-warning' onClick={handlClick}>Valider</button>
       </div>
        <div>
           
                {content?content.map((e)=>{
                    return <ul style={{"listStyleType":"none"}}>
                        <li key={e.id} className="d-flex  justify-content-around">
                         <span>{e.elm}</span>
                         <button className='btn btn-danger' onClick={()=>handlDelete(e.id)}>DELETE</button>
                        </li>
                    </ul>
                }):""}
            
        </div>
    </div>
  )
}
