import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { Ajouter ,Supremier} from './store'
export default function AfficherTab() {
    
    let afichage=useSelector((data)=>data.table.tbl)
    const dispatch=useDispatch()
    const[nomP,setnomP]=useState()
    const[prix,setPrix]=useState()

    const handlClick=()=>{
        dispatch(Ajouter({
            id:afichage.length+1,
            nomP:nomP,
            prixP:prix
        }))
    }


  return (
    <div>
        <input type='text' onc/>
        <input type='text' />
        <button onClick={handlClick}>Ajouter</button><br/>
        <table>
            <tr>
                <th>nom produis</th>
                <th>prix produis</th>
                <th>Action</th>
            </tr>
            {afichage?afichage.map((r)=>{
                return <tr>
                    <td>{r.nomP}</td>
                    <td>{r.prixP}</td>
                    <td><button>Delete</button></td>
                </tr>
            }):''}
        </table>
    </div>
  )
}
