import React,{useState} from 'react'

class Product{
    constructor(n,p,q){
        this.nom=n
        this.prix=p
        this.qte=q

    }
}

function LignePanier(props){

    return(
        <tr>
            <td>{props.prod.nom}</td>
            <td>{props.prod.prix}</td>
            <td>{props.prod.qte}</td>
            <td>{props.prod.prix * props.prod.qte}</td>
        </tr>
    )
}

function Panier(props){

    const total=props.products.reduce((s,e)=> s+ e.prix *e.qte,0,)

    return(
        <>
            <h2>Total à payer: {total}</h2>
            <br></br>
            <table>
                <th>Contenu de votre panier</th>
                <tr>
                    <td>Nom du produit</td>
                    <td>Prix</td>
                    <td>Quantité</td>
                    <td>Prix à payer</td>
                </tr>
                {

                    props.products.map((x)=> <LignePanier prod={x} /> )

                }


            </table>
 
        </>
    )
}

export default function FormAchat(){
    const [tp,setTp]=useState([])

    const [nom, setNom]=useState('')
    const [prix,setPrix]=useState(0)
    const [qte,setQte]=useState(0)

    function ajouter(){

        if(nom && prix && qte){

            let p=new Product(nom,prix,qte)
            if(tp.length>0){
            const y = tp.find((e)=> e.nom===nom)
            
            if(!y){//si le nom de produit n'est pas dans le panier
                    setTp([...tp,p])
            }else{
                alert('Ce produit est deja dans le panier')
            }
            }else{
            setTp([...tp,p])
            }
        }else{
            alert('saisir des informations correctes')
        }
    }

    return(
        <div>
            <h1>Remplir votre panier</h1>
            
                <label>Nom du produit.......: </label>
                <input type="text" onChange={(e)=> setNom(e.target.value) } />
                <br></br>
                <label>Prix du produit..........: </label>
                <input type="number" onChange={(e)=> setPrix(e.target.value) } />
                <br></br>
                <label>Quantité du produit: </label>
                <input type="number" onChange={(e)=> setQte(e.target.value) } />
                <button onClick={ajouter}>Ajouter au panier</button>
            
            <Panier products={tp}/>

        </div>




    )

}
