import React from 'react'
import { useState } from 'react'

export default function App() {
  const vols=[ 
    {numVol:1,depart:"Fes",Arrivee:"Madrid", 
    places:[ 
    {numPlace:1,tarif:1000, libre:true}, 
    {numPlace:2,tarif:1000, libre:true }, 
    {numPlace:3,tarif:1000, libre:true }, 
    ]}, 
    {numVol:2,depart:"Fes",Arrivee:"London", 
    places:[ 
    {numPlace:1,tarif:2500, libre:true }, 
    {numPlace:2,tarif:3000, libre:true }, 
    {numPlace:3,tarif:2500, libre:true }, 
    ]}, 
    {numVol:3,depart:"Fes",Arrivee:"Bern", 
    places:[ 
    {numPlace:1,tarif:3500, libre:true }, 
    {numPlace:2,tarif:3000, libre:true}, 
    {numPlace:3,tarif:4000, libre:true}, 
    ]} 
   ] 

    const[idL,setIdL]=useState()
    const[ligne,setLigne]=useState()
    const[arret,setArret]=useState()

    function handlChercher(){
      setLigne(lignes.find((lign)=>lign.id===idL))
    }

    function Ajouter_arrets(){
      ligne.arrets.push({codeA:arret,ordreA:ligne.arrets.length+1})
      setArret((prev)=>({...prev,arrets:ligne.arrets}))
    }
    
    function supremier(i){
      ligne.arrets.splice(i,1)
      setArret({...ligne,arrets:ligne.arrets})
   }

  return (
    <div>
    
    </div>
  )
}
