import React from 'react';
import './App.css';
import { useState } from 'react'
import Addlist from './components/Addlist';
export default function App() {
    const list=[
        { id:1,
          name:"HP",
          price:200,
          quantit:3
        },
        { id:2,
          name:"samsung",
          price:400,
          quantit:2
        },
        { id:3,
          name:"lenovo",
          price:100,
          quantit:2
        },
      ]
      //  const list=[];
      const [lists,setLists]=useState(list);
      const[updateSetate,setUpdateState]=useState(-1);
      const total=lists.reduce((s,e)=> s+ e.price *e.quantit,0,)
      function deletList(id){
        const newlist=lists.filter((li)=>li.id !== id)
        setLists(newlist)
      }

      function handelEdit(id){
        setUpdateState(id)
      }
      function handleSubmit(event){
        const name=event.target.elements.name.value;
        const price=event.target.elements.price.value;
        const quantit=event.target.elements.quantit.value;
      }

      function EditList({curent,lists,setlest}){
        function handInput(event){
            const name=event.target.value;
            const value=name.value;
        }
        return(
            <tr>
                <td><input type="text" name='name' value={curent.name}/></td>
                <td><input type="text" name='price' value={curent.price}/></td>
                <td><input type="text" name='quantit' value={curent.quantit}/></td>
                <td><button type='submit'>valider</button></td>
            </tr>
        )
      }
      return (
        <div className='text-center'>
       
            <Addlist setlst={setLists} EditList={EditList}/>
            <form onSubmit={handleSubmit}> 
           <div>total: <span id="total">{total}</span></div>
          <table style={{border:"1px solid red"}}>
            <tr style={{border:"1px solid red"}}>
              <th style={{border:"1px solid red"}}>nom produit</th>
              <th style={{border:"1px solid red"}}>price</th>
              <th style={{border:"1px solid red"}}>Quantité</th>
              <th colspan='2' style={{border:"1px solid red"}}>action</th>
            </tr>
            {
              lists.map((e)=>
              updateSetate===e.id?<EditList curent={e} lists={lists} setlest={setLists}/>:
                <tr style={{border:"1px solid red"}}>
                  <td style={{border:"1px solid red"}}>{e.name}</td>
                  <td style={{border:"1px solid red"}}>{e.price}</td>
                  <td style={{border:"1px solid red"}}>{e.quantit}</td>
                  <td style={{border:"1px solid red"}} onClick={()=> handelEdit(e.id)}><button>Edite</button></td>
                  <td style={{border:"1px solid red"}} onClick={()=>deletList(e.id)}><button>Delete</button></td>
                </tr>
              )
            }
          </table>
          </form>
        </div>
       
      )
}
