import React from 'react'
import { useState } from 'react'

export default function App() {
  const list=[
    { id:1,
      name:"HP",
      price:3000
    },
    { id:2,
      name:"samsung",
      price:4500
    },
    { id:3,
      name:"lenovo",
      price:5000
    },
  ]
   const [affch,setAfch]=useState(list)
   function ajouter(event){
    event.preventDefault()
    const name=document.getElementById('name').value;
    const price=document.getElementById('price').value;
    const newlist={
      id:affch.length+1,
      name,
      price
    }
    setAfch((l)=>l.concat(newlist))
   }
   function DeleteElm(id){
    setAfch(affch.filter((d)=>d.id!==id))
   }
   var total=affch.map((e)=>e+=e.price)
  return (
    <div>
      <form className='m-3' onSubmit={ajouter} >
        <input type="text"id="name" name="name" placeholder='Enter Name'/>
        <input type="text" id='price' name="price" placeholder='Enter price'/>
        <button type='submit'>add</button>
      </form>


      <table style={{border:"1px solid red"}}>
            <tr style={{border:"1px solid red"}}>
              <th style={{border:"1px solid red"}}>nom produit</th>
              <th style={{border:"1px solid red"}}>price</th>
              <th colspan='2' style={{border:"1px solid red"}}>action</th>
            </tr>
            {affch.map((e)=>
            <tr style={{border:"1px solid red"}}>
              <td style={{border:"1px solid red"}}>{e.name}</td>
              <td style={{border:"1px solid red"}}>{e.price}</td>
              <td style={{border:"1px solid red"}}><button onClick={()=>DeleteElm(e.id)}>Delete</button></td>
            </tr>)}
            <div>le total: {total}</div>
      </table>
   
    </div>
  )
}
