import React from 'react'
import { useParams } from 'react-router'

export default function SingleProdct({productsAp}) {
  const {id}=useParams()
  const product=productsAp.find((pr)=>pr.id==parseInt(id))
  return (
    <div>{product.map((elm,index)=>{
      return <div key={index} className="card shadow-sm col-3">
      <img className="bd-placeholder-img card-img-top"src={elm.thumbnail} alt="" />
      <div className="card-body">
      <p className="card-title">{elm.title}</p>
      <p className="card-text">{elm.price}</p>
      </div>
    </div>
    })}</div>
  )
}
