import React,{useState} from 'react';
import { Link } from 'react-router-dom';

const App = () => {

    const trins = [
        {id:'t1', nom:'bruq', villes:[] },
        {id:'t2', nom:'gazel',  villes:[] },
        {id:'t3', nom:'wins', villes:[] },
        {id:'t4', nom:'fennec',  villes:[] }
    ]

    const [traunID, setTrainId] = useState()
    // const [nomville, setNomville] = useState()
    const [tab, setTab] = useState([])
    const[ville,setVille]=useState()

    function Rechercher()
    {
        setTab(trins.find((trn) => trn.id == traunID));
    }

     function ajouter_ville()
    {
        tab.villes.push({nom_ville:ville});
        setTab((prev)=>({
            ...prev,villes:tab.villes
        }))
    }


    function supremier(i){
       setTab(tab.villes.filter((f)=>f!==i))
    }
    return (
        <div>
            <h1>Suivi des vélos Electriques</h1>
            id <input type="text" onChange={(event) => setTrainId(event.target.value)} /> <br />
            nom <input type="text" value={(tab)?tab.nom:''} />
            <button onClick={() => Rechercher()}>Rechercher</button> <br />
           ville depart: <input type="text" />
            ville arrivée <input type="text"  />
            nouvelle_ville de passage <input type='text' onChange={(e)=>setVille(e.target.value)}/>
            <button onClick={()=>ajouter_ville()}>Ajouter la ville</button>
            <table>
                <tr>
                    <th>id parcours</th>
                    <th>distance</th>
                </tr>

                {
                    (tab.villes)?tab.villes.map((v,i) => {
                        return <tr>
                            <td> {v.nom_ville} </td>
                            <td> {i} </td>
                            <td> <button onClick={()=>supremier(i)}>delete</button> </td>
                        </tr> 
                    }):''
                }

            </table>
        </div>
    );
}

export default App;
















