import React, { useState } from 'react'
export default function App(){
  const diplomes=['bac','technicien','technicien','specialisé','licence','master']
const [niveau,setNiveau]=useState()
    return(
      <div>
        <input type='text' onChange={(e)=>setNiveau(e.target.value)} />
        {diplomes.map((e)=>{
          let clr=null
          if(e===niveau){
            clr={color:'white',backgroundColor:'red'};
          }
          return <ul style={{color:'green',listStyleType:'none'}}>
            <li style={clr}>{e}</li>
          </ul>
        })}
      </div>
    )
}
