import initial from "../../Datas";
const Initialreducer=(state=initial[0],action)=>{
    switch(action.type){
        case 'AJOUTERPAY':
            return {...state,pays:[...state,action.payload]};
        case 'MODIFIER_PUPLATION':
            const pays=state.find((p)=>p.code=== parseInt(action.payload.code))
            if(pays){
                pays.name=action.payload.name
                pays.SurfaceArea=action.payload.SurfaceArea
                pays.Population=action.payload.Population
            }
            return state
        case 'SUPREMIER_PUPLATION':
            return {...state,pays:[...state.filter((f)=>f.code!==parseInt(action.payload))]};
        default :
            return state;
    }
}

export default Initialreducer
