import React, { useState } from 'react';
import './App.css';
function App() {
  let[nom,setnom]=useState('choisir le nom');
  let chnge=()=>{
    setnom(nom=`${document.getElementById('inpt').value}`)
  }
  return (
    <div className='App'>
      <input type='text' id='inpt'/>
      <br/>
      <button onClick={chnge} >click</button>
      <div>{nom}</div>
    </div>
   
  )
}
export default App
