import React from 'react'
import { useState } from 'react'

export default function App() {

  const Comptes=[
    {num:1,nom:"Ali",solde:18000,operatios:[
      {type:"Credit",montant:10000},{type:"Debit",montant:8000}]},
    {num:2,nom:"Sarah",solde:45000,operatios:[
        {type:"Credit",montant:10000},{type:"Debit",montant:2000}]},
  ]

  const[idOp,setIdOp]=useState()
  const[compt,setCompt]=useState()
  const[Montant,setMontant]=useState()

  function handleChercher(){
    setCompt(Comptes.find((f)=>f.num==idOp))
  }

  // function Crediter(){
  //   compt.operatios.push({type:'Credit',montant:Montant})
  //   setCompt((prev)=>({...prev,operatios:compt.operatios}))
  // }

  const handleCredit = () => {
    compt.operatios.map(op=>op.type==="Credit"?op.montant+=Number(Montant):"")
    setCompt({...compt, solde : compt.solde  +Montant})
    // const compteTrouve=compt.find(c=>c.num===idOp)
    // compteTrouve.operatios.push({type:'Credit',Montant})
    // compteTrouve.solde+=Montant;
    // setCompt((prev)=>({...prev,operatios:compteTrouve}))
  }
 

  return (
    <div>
      no Compte <input type='text' onChange={(e)=>setIdOp(e.target.value)}/>
      <button onClick={handleChercher}>Rechercher</button> <br/>
      Marque: <b>{compt?compt.nom:''}</b><br/>
      Nombre de verres: <b>{compt?compt.solde:''}</b><br/>
      Montant operation <input type='text' onChange={(e)=>setMontant(e.target.value)}/>
      <button onClick={()=>handleCredit()}>Crediter</button>
      <button>Debiter</button><br/>
      <table>
        <tr>
          <th>Type operation</th>
          <th>Montant</th>
        </tr>
        {compt&&compt.operatios.map((r,indx)=>{
          return <tr key={indx}>
            <td>{r.type}</td>
            <td>{r.montant}</td>
          </tr>
        })}
      </table>
    </div>
  )
}
