// import User from './components/User';
import React, { Component } from 'react';
class App extends Component {
  constructor(props){
    super(props);
    this.state={
      name:'Mohamed',
      age:'20'
    }
  }
    
    chang=()=>{
      this.setState({
        name:'Ali',
      age:'30'
      })
    }
    render() { 
        return (
          <div>
         <button onClick={this.chang}>chnage</button>
         <div>{this.state.name}</div>
         <div>{this.state.age}</div>
           </div>
          );
    }
}
export default App;
