import React from 'react'
import { useState } from 'react'

export default function App() {
    const vols=[
        {numVol:1,depart:"Fes",Arrivee:"Madrid"},
        {numVol:2,depart:"Fes",Arrivee:"London"},
        {numVol:3,depart:"rabat",Arrivee:"Bern"}
    ]
    const [numVol, setNumVol]=useState();
  return (
   <div>
     <div>
        Numero de vole: <input type="text" onChange={(e)=>setNumVol(vols.find((x)=>x.numVol==e.target.value))}></input> 
        <button>next{'>>'}</button>
        <div>
            <div>Ville de départ: {numVol?numVol.depart:''}</div>
            <div>Ville d'arrivée: {numVol?numVol.Arrivee:''} </div>
        </div>
    </div>
   </div>
  )
}
