import React from 'react';
import { useState } from 'react';
export default function App(){
  const[nomber1,setNomber1]=useState()
  const[operation,setOperation]=useState()
  const[nomber2,setNomber2]=useState()
  const[relst,setResult]=useState()

  function calculer(){
    if(operation==='choisir'){
      setResult('choisir un operateur SVP!')
    }else if(operation==='+'){
      setResult(parseFloat(nomber1)+parseFloat(nomber2))
    }
  }
  const oper=['+','-','x','/']
  return(
    <div>
      <input type='number' onChange={(e)=>setNomber1(e.target.value)}/>
      <select onChange={(e)=>setOperation(e.target.value)}>
      {/* <option value='choisir'>choisir</option>
      <option value='+'>+</option>
      <option value='x'>x</option>
      <option value='/'>/</option>
      <option value='-'>-</option> */}
      {oper.map((op)=>{
        return <option value={op}>{op}</option>
      })}
       </select>
      <input type='number'onChange={(e)=>setNomber2(e.target.value)}/>
      <button onClick={()=>calculer()}>calculer</button>
     <div>resultat :<b style={{color:'red'}}>{relst}</b> </div>
    </div>
  )
}
