import React from 'react';
import img1 from "./pictures/HP16D0195NF.jpg"
import img2 from "./pictures/HP14424U3EA.jpg"
import img3 from "./pictures/NXATHEF002.jpg"
import img4 from "./pictures/HUA6901443442959.jpg";
export default function Produits() {
  const products = [
    {
    id: 1,
    title: 'PC Portable Gamer HP VICTUS',
    price: '7490 DH',
    thumbnail : img1
    },
    {
    id: 2,
    title: 'PC Portable Gamer HP VICTUS',
    price: '2190 DH',
    thumbnail : img2
    },
    {
    id: 3,
    title: 'Pc Portable Chromebook Acer',
    price: '3640 DH',
    thumbnail: img3
    },
    {
    id: 4,
    title: 'PC Portable - HUAWEI',
    price: '1270 DH',
    thumbnail: img4
    }];
  return (
 
    <div className='d-flex justify-content-center'>
      <div className='card shadow-sm col-11 p-2'>
        <h3 className='text-primary'>Ordinateurs portable </h3>
        <div className="row">
          {products.map((elm,index)=>{
            return <div key={index} className="card shadow-sm col-3">
            <img className="bd-placeholder-img card-img-top"src={elm.thumbnail} alt="" />
            <div className="card-body">
            <p className="card-title">{elm.title}</p>
            <p className="card-text">{elm.price}</p>
            <div className="d-flex justify-content-between align-items-center">
              <div className="btn-group">
              <button className="btn btn-sm btn-outline-secondary">Ajouter au panier</button>
              </div>
            </div>
            </div>
          </div>
          })}
        
        </div>
      </div>
    </div>

  )
}
