import React from 'react'
import { useState } from 'react'
export default function App() {
    const vols=[
        {numVol:1,depart:"Fes",Arrivee:"Madrid"},
        {numVol:2,depart:"Fes",Arrivee:"London"},
        {numVol:3,depart:"rabat",Arrivee:"Bern"}
    ]
    const [numVol, setNumVol]=useState();
    const [vol,setVol]=useState()
    function rechercherVol(){
        if(!numVol){
            alert('vide')
          }else{
            setVol(vols.find((e)=>e.numVol==numVol))
          }
    }
  return (
   <div>
     <div>
        Numero de vole: <input type="text" onChange={(e)=>setNumVol(e.target.value)}></input> 
        <button onClick={rechercherVol}>Rechercher</button>
        <div>
            <div>Ville de départ: {vol?vol.depart:''}</div>
            <div>Ville d'arrivée: {vol?vol.Arrivee:''} </div>
        </div>
    </div>
   </div>
  )
}
