import initial from "../../Datas";
const Initialreducer=(state=initial,action)=>{
    switch(action.type){
        case 'AJOUTERPAY':
            return {...state,Ajpay:[...state[0].pays,action.payload]};
        case 'MODIFIER_PUPLATION':
            const pays=state.pays.find((p)=>p.code=== parseInt(action.payload))
            if(pays){
                pays.Population=action.payload.Population
            }
            return state
        default :
            return state;
    }
}

export default Initialreducer
