import React from "react";
import "./App.css";
import jurys from "./dataNote";
import AddProjet from "./AddProjet";

export default class Projet extends React.Component {
  constructor(
    id,
    titre,
    noteJury1 = -1,
    noteJury2 = -1,
    noteJury3 = -1,
    noteJury4 = -1
  ) {
    super();
    this.id = id;
    this.titre = titre;
    this.note = [noteJury1, noteJury2, noteJury3, noteJury4];
  }
  nombreDeNotes() {
    var c = 0;
    for (let x = 0; x < 4; x++) {
      if (this.note[x] !== -1) {
        c++;
      }
    }
    return c;
  }

  envoyerNote(codeJury, note) {
  
      if (jurys.find((jury) => jury.codeSecret == codeJury)) {
        for (let x = 0; x < 4; x++) {
          if (this.note[x] === -1) {
            this.note[x] = note;
            return;
        };
      }
      }  
      else return (0);
      
    
  }


}
