import React from 'react'
import { useSelector, useDispatch } from "react-redux"; 
import { decrement,increment, reset,} from "../Redux/actions/reduxActions"; 
export default function PageIncrement() {
  const counter = useSelector((state) => state); 
 const dispatch = useDispatch(); 
  return (
    <div>
      <h2>{counter}</h2>
 <button style={{margin:"20px", fontSize:"20px", width:"10%"}}
onClick={() => dispatch(increment())}>Increase</button>
 <button style={{margin:"20px", fontSize:"20px", width:"10%"}}
onClick={() => dispatch(reset())}>Reset</button>
 <button style={{margin:"20px", fontSize:"20px", width:"10%"}}
onClick={() => dispatch(decrement())}>Decrease</button>
    </div>
  )
}
