  function affichageFinal(){
    setResltat(afch)
  }
  return (
    <div>
      <input type='text' onChange={(e)=>setAfich(e.target.value)}/>
      <button onClick={affichageFinal}>afficher</button>
      <div>ResultatList: {resltat}</div>
    </div>
  )
}
