const ConunterReducer=(state = {val:0}, action)=>{
    switch(action.type){
        case "INCREMENT" :
            return {...state, val : state.val + 1};
        case "DECREMENT" :
            return {...state, val : state.val===0};
        case "RESET": 
            return {...state, val :0}; 
        default :
            return state;
    }
}
export default ConunterReducer;
