import React, { useState } from 'react'
import './App.css';
import plantList from './datas/plantList';
import ProductList from './components/ProductList';
import Cards from './components/Cards';
export default function App() {
  const[product,setProduct]=useState(plantList)
  const[cartItems,setCartItems]=useState([])
  const total=cartItems.reduce((s,e)=> s+ e.price *e.quantity,0,)
  const addTocart=(item)=>{
    let producItem=cartItems.find(product=>product.id===item.id)
    if(producItem){
      producItem.quantity+=1;
      setCartItems([...cartItems])
    }else{
      item.quantity=1;
      setCartItems([item,...cartItems])
    }
  }
  
  return (
    <div>
        <header id='bnr'><img src='logo.png' id='imglogo'/>
        <span id='logo'>La maison jungle</span>
        </header>
        <div>
          <section id='banner'>
            <div className='d-flex'>
              <Cards cartItems={cartItems} total={total}/>
              <ProductList products={product} addTocart={addTocart}/>
            </div>
          </section>
        </div>
    </div>
  )
}
