import React, { useState }  from "react";
import CherchBar from "./components/CherchBar";
import ResultatList from "./components/ResultatList";
export default function App(){
  const[flt,setFlt]=useState()
  const list=[
    {nom:"banane",type:"fruit"},
    {nom:"orange",type:"fruit"},
    {nom:"pomme",type:"fruit"},
    {nom:"raisins",type:"fruit"},
    {nom:"kiwi",type:"fruit"},
    {nom:"tomate",type:"legume"},
    {nom:"carotte",type:"legume"},
    {nom:"pomme de terre",type:"legume"},
    {nom:"navet",type:"legume"},
    {nom:"poivron",type:"legume"}
    ]

    function filtrage(typ){
      setFlt(list.filter((f)=>f.type===typ))
    }
  return(
    <div className='d-flex justify-content-center'>
    <div className="shadow-lg m-2 col-6">
      <h4 className="text-success my-3 ms-3">Composent App</h4>
      <CherchBar filtrage={filtrage}/>
      <h5 className="text-danger ms-3">le type :{flt}</h5>
      <ResultatList fltt={flt}/>
    </div>
    </div>
  )
}
