import React from 'react'
import { useState } from 'react'
export default function Comp1() {
  const list=[
    { id:1,
      name:"HP",
      price:3000
    },
    { id:2,
      name:"samsung",
      price:4500
    },
    { id:3,
      name:"lenovo",
      price:5000
    },
  ]
  const [lists,setLists]=useState(list)
  return (
    <div>
      <table style={{border:"1px solid red"}}>
        <tr >
          <th>nom produit</th>
          <th>price</th>
        </tr>
        {
          lists.map(e=>{
            <tr>
              <td>{e.name}</td>
              <td>{e.price}</td>
            </tr>
          })
        }
      </table>
    </div>
   
  )
}
