import React, { useState } from 'react'
import './App.css'
export default function App() {
    const courses=[ 
               {id: "MarcheF",nom:"asmae",categorie:"F", distance:10000}, 
               {id: "MarcheF",nom:"salma",categorie:"F", distance:10000}, 
               {id: "MarcheF",nom:"doha",categorie:"F", distance:10000}, 
               {id: "MarcheH",nom:"Marche hommes",categorie:"H", distance:10000}, 
               {id: "MarathonF",nom:"Manal",categorie:"F", distance:42000}, 
               {id: "MarathonH",nom:"yassin",categorie:"H", distance:42000} 
               ] ;
        const[search,setSearch]=useState('')
  return (
    <div>
      <input type='text' onChange={(e)=>setSearch(e.target.value)}/>
      <table className='table table-bordered'>
        <tr>
            <th>Id</th>
            <th>Nom</th>
            <th>Categorie</th>
            <th>Distance</th>
        </tr>
        {courses.filter((etm)=>{
            return search.toLocaleLowerCase()===""?etm:etm.id.toLocaleLowerCase().includes(search)}
            ).map((ex,id)=>{
                return <tr key={id}>
                <td>{ex.id}</td>
                <td>{ex.nom}</td>
                <td>{ex.categorie}</td>
                <td>{ex.distance}</td>
            </tr>
              
            })
            }
      </table>
    </div>
  )
}








