import React, { useState } from 'react'

export default function App() {
  const[identif,setIdentif]=useState()
  const[DateN,setIDateN]=useState()
  const[ville,setVille]=useState('')

  const[Loisirs,setILoisirs]=useState([])
  const[afficher,setAfficher]=useState()
  const lois=[]

  function afficherInfo(event){
    lois.push(Loisirs)
    event.preventDefault();
    setAfficher(`je suis ${identif} né le ${DateN} à ${ville} le genre ${genre} 
    et mes loisirs sont `+lois)

  }

  return (
    <div className='text-center'>
      <form onSubmit={afficherInfo}>
      <h2 className='text-primary'>Inscriptipn</h2>

      <div>
        <b className='m-2'> Ville</b>
        <select onChange={(e)=>setVille(e.target.value)}>
          <option value="choisir">choisir</option>
          <option value="casablanca">casablanca</option>
          <option value="fes">fes</option>
          <option value="rabat">rabat</option>
        </select>
      </div>

      <div>
        <b>Loisirs</b>
        <input type='checkbox' onClick={(e)=>setILoisirs(e.target.value)} value="Sport" name='Loisirs'/> <label>Sport</label> 
        <input type='checkbox' onClick={(e)=>setILoisirs(e.target.value)} value="Lecteur" name='Loisirs'/> <label>Lecteur</label> 
        <input type='checkbox' onClick={(e)=>setILoisirs(e.target.value)} value="Musique" name='Loisirs'/> <label>Musique</label> 
      </div>

      <div>
        <button className='btn btn-primary my-3'>S'inscrire</button>
      </div>
      </form>
      <h4>{afficher}</h4>

    </div>
  )
}
