import { useState } from 'react';
export default function App() {
    const [slct,setSlct]=useState()
    let elments=["orange","red","blue",'green',"yellow"];
    const handlerChange=(event)=>{
      setSlct(event.target.value)

      }
   return(
    <div>
      <h2>{slct}</h2>
     <select onChange={(e)=>handlerChange(e)}>
     {elments.map(function(option, index) {
  return <option key={index+1}
  value={option}>{option}</option>})
  }
     </select>
    </div>
   )
  }
