import React from 'react'
import { useState } from 'react'

export default function App() {
  const velo=[
    {ref:"v1",marque:'fennce',automotie:0,kilometrage:0,parcours:[]},
    {ref:"v2",marque:'dacia',automotie:0,kilometrage:0,parcours:[]},
    {ref:"v3",marque:'wins',automotie:0,kilometrage:0,parcours:[]},
    {ref:"v4",marque:'fennce',automotie:0,kilometrage:0,parcours:[]},
  ]
  const [Numvelo,setnumVelo]=useState()
  const [listVelo,setListvelo]=useState()
  const[nbrmunit,setnbrmunit]=useState(0)
  function rechercher(){
    setListvelo(velo.find((e)=>e.ref==Numvelo))
  }
  return (
    <div>
    <div> ref <input type='text' onChange={(e)=>setnumVelo(e.target.value)}/> </div>
    <div> Marque <input type='text' value={listVelo?listVelo.marque:""}/> 
    <button onClick={rechercher} >rechercher</button> </div>
    <div> auto <input type='text' value={nbrmunit}/> </div>
    <div> kilo <input type='text' value={listVelo?listVelo.kilometrage:""}/> </div>
    <div> nbr munit <input type='text' onChange={(e)=>setnbrmunit(e.target.value)}/>  <button>charger</button> </div>
    <div> distance parciur <input type='text'/>  <button>Ajouter parcours</button> </div>
    </div>
  )
}
