import React, { useState } from 'react'

export default function App() {
  const[list,setList]=useState([])
  const[id,setId]=useState()
  const[nom,setNom]=useState()
  const[prix,setPrix]=useState()

    function remplir(){
      list.push({idd:l,name:nom,Pris:prix})
      setList([...list])
      setNom('')
      setPrix('')
    }

    function handlDelete(n){
     setList(list.filter((l)=>l.name!==n))
    }
  return (
    <div>
       <input type="text" value={nom} placeholder='Enter Name' onChange={(e)=>setNom(e.target.value)}/>
        <input type="text" value={prix} placeholder='Enter price'onChange={(e)=>setPrix(e.target.value)}/>
        <button className='btn btn-primary' onClick={remplir}>add</button>

        <br/>

       <table className='table table-bordered text-center mt-3'>
        <tr>
            <th>id</th>
            <th>nom</th>
            <th>prix</th>
            <th>action</th>
        </tr>
        {list&&list.map((rslt,i)=>{
        return<tr key={i}>
           <td >{i+1}</td>
           <td >{rslt.name}</td>
           <td >{rslt.Pris}</td>
           <td><button className='btn btn-danger' onClick={()=>handlDelete(rslt.name)}>suppremier</button></td>
        </tr>
        })}

        </table>
    </div>
  )
}
