import React,{useState} from 'react';

const App = () => {

    const velos = [
        {ref:'v1', marque:'ford', autonomie:0, kilometrage:0, parcours:[] },
        {ref:'v2', marque:'ford', autonomie:0, kilometrage:0, parcours:[] },
        {ref:'v3', marque:'ford', autonomie:0, kilometrage:0, parcours:[] },
        {ref:'v4', marque:'ford', autonomie:0, kilometrage:0, parcours:[] }
    ]

    const [veloRef, setVeloRef] = useState()
    const [mint, setmint] = useState()
    const [distance, setDistance] = useState()
    const [parcour, setParcour] = useState([])

    function Rechercher()
    {
        setParcour(velos.find((v) => v.ref === veloRef));
    }

    function charger()
    {
      parcour.autonomie += (mint<100)? mint : 100;
        setParcour((prev)=>({
            ...prev,autonomie:parcour.autonomie
        }))
    }
    
    function ajouter_parcours()
    {
      if(parcour.autonomie > distance)
        {parcour.parcours.push({id:parcour.parcours.length+1, distance:distance});
        setParcour((prev)=>({
            ...prev,parcours:parcour.parcours
        }))}
    }

    return (
        <div>
            <h1>Suivi des vélos Electriques</h1>
            reference <input type="text" value={veloRef} onChange={(event) => setVeloRef(event.target.value)} />
             <button onClick={() => Rechercher()}>Rechercher</button><br/>
            Marque :{(parcour)?parcour.marque:''}
            <br />

            Autonomie en [km] :{(parcour)?parcour.autonomie:''}
            <br />

            kilométrage :{(parcour)?parcour.kilometrage:''}
            <br />

            nombre de mintute <input type="text" value={mint} onChange={(event) => setmint(Number(event.target.value))}  />
            <button onClick={() => charger()}>charger</button> <br />

            distance de parcours <input type="text"  onChange={(event) => setDistance(Number(event.target.value))}  />
            <button onClick={ajouter_parcours}>ajouter parcours</button>
            <br />

            <parcourle>
                <tr>
                    <th>id parcours</th>
                    <th>distance</th>
                </tr>

                {
                    (parcour.parcours)?parcour.parcours.map((velo) => {
                        return <tr>
                            <td> {velo.id} </td>
                            <td> {velo.distance} </td>
                        </tr> 
                    }):''
                }

            </parcourle>
        </div>
    );
}

export default App;
