import './App.css';
import Addlist from './components/Addlist';
export default function App() {
  const list=[
    { id:1,
      name:"HP",
      price:3000
    },
    { id:2,
      name:"samsung",
      price:4500
    },
    { id:3,
      name:"lenovo",
      price:5000
    },
  ]
  const[afchlst,setAfchlst]=useState(list)
  return (
    <div>
      <table>
        <tr>
          <th>id</th>
          <th>name</th>
          <th>price</th>
        </tr>
        {afchlst.map(e=><tr key={e.id}>
          <td>{e.id}</td>
          <td>{e.name}</td>
          <td>{e.price}</td>
        </tr>)}
      </table>
      <Addlist setlitaf={setAfchlst} id={id}/>
    </div>
  )
}
