import React from 'react'
export default function App(){
  const diplomes=['bac','technicien','technicien','specialisé','licence','master']
  let niveau='specialisé'
    return(
      <div>
        {diplomes.map((e)=>{
          var clr=''
          if(e===niveau){
            clr={color:'red'};
          }
          return <ul style={{color:'green',listStyleType:'none'}}>
            <li style={{clr}}>{e}</li>

          </ul>
        })}
      </div>
    )

}
