import React,{Component} from 'react';
import './App.css';
class App extends Component{
  state={
    name:""
  }
  blorFnctn=(evnt)=>{
    this.setState({
      name:evnt.target.value
    })

  }
  render(){
    return(
      <div className='App'>
        <input type="text" onChange={this.blorFnctn}/>
        <button>Valider</button>
        <h1>{this.state.name}</h1>

      </div>
    )
  }
}export default App;
