import React,{useState} from 'react';
import GaimeRandom from'./components/GaimeRandom'
export default function App(){
  const[Start,setStart]=useState(0)
  const[click,setClick]=useState(0)
  const[vue,setvue]=useState(0)

  function handlclick(){
    setClick(Start)
    setvue(1)
  }
  return(
    <div className='text-center'>
      <input type='number' placeholder='entrez nomber entre 1 et 6' max="6" min='1' onChange={(e)=>setStart(e.target.value)}/>
      <button onClick={handlclick}>valider</button>
        {vue==1?<GaimeRandom click={click}/>:""}

    </div>
  )
}
