import React from 'react';
import './App.css';
import { useState } from 'react'
export default function App() {
      const list=[];
      const [lists,setLists]=useState(list);
      const total=lists.reduce((s,e)=> s+ e.price *e.quantit,0,)
      
      function deletList(id){
        const newlist=lists.filter((li)=>li.id !== id)
        setLists(newlist)
      }

      function handleSubmit(event){
        event.preventDefault()
        const name=event.target.elements.name.value;
        const price=event.target.elements.price.value;
        const quantit=event.target.elements.quantite.value;
        const newlist={
          id:1,
          name,
          price,
          quantit
        }
        if(name==="" || price==="" || quantit===""){
          alert('verifie')
        }else{
          setLists((l)=>{
          return l.concat(newlist)
        })
        }
      }
      return (
        <div className='text-center'>
          <form onSubmit={handleSubmit} >
        <input type="text" name="name" placeholder='Enter Name'/>
        <input type="text" name="price" placeholder='Enter price'/>
        <input type="text" name="quantite" placeholder='Enter quantite'/>
        <button type='submit' style={{ backgroundColor:"aqua"}}>add</button>
      </form>

           <div>total: <span id="total">{total}</span></div>
          <table style={{border:"1px solid red"}}>
            <tr style={{border:"1px solid red"}}>
              <th style={{border:"1px solid red"}}>nom produit</th>
              <th style={{border:"1px solid red"}}>price</th>
              <th style={{border:"1px solid red"}}>Quantité</th>
              <th colspan='2' style={{border:"1px solid red"}}>action</th>
            </tr>
            {
              lists.map((e)=>
                <tr style={{border:"1px solid red"}}>
                  <td style={{border:"1px solid red"}}>{e.name}</td>
                  <td style={{border:"1px solid red"}}>{e.price}</td>
                  <td style={{border:"1px solid red"}}>{e.quantit}</td>
                  <td style={{border:"1px solid red"}} >
                    <button onClick={()=>deletList(e.id)}>Delete</button></td>
                </tr>
              )
            }
          </table>
        </div>
       
      )
}




