import React, { useState } from 'react';
import './App.css';
function App() {
  let products=['tooth paste','tooth brush','']
  const [search,setSerch]=useState('');
  const handleinput=(e)=>{
    setSerch(e.target.value)
  }
  const clear=(e)=>{
    setSerch("")
  }
  const DesplayBtn=search.length>0;//comme condition ila kan 0 aykon false kan kbr mn 0 aykon true
  
  return (
    <div className='App'>
      <input type="text" value={search} onChange={handleinput}/>
     <br/>
     {DesplayBtn && <button onClick={clear}>Clear</button>}
     
    </div>
  )
}
export default App
