import React,{useState} from 'react';

const Velo = () => {

    const velos = [
        {ref:'v1', marque:'ford', autonomie:0, kilometrage:0, parcours:[] },
        {ref:'v2', marque:'ford', autonomie:0, kilometrage:0, parcours:[] },
        {ref:'v3', marque:'ford', autonomie:0, kilometrage:0, parcours:[] },
        {ref:'v4', marque:'ford', autonomie:0, kilometrage:0, parcours:[] }
    ]

    const [veloRef, setVeloRef] = useState()
    const [min, setMin] = useState()
    const [distance, setDistance] = useState()
    const [tab, setTab] = useState([])

    function Rechercher()
    {
        setTab(velos.find((v) => v.ref === veloRef));
    }

    function charger()
    {
        tab.autonomie += (min<100)? min : 100;
        setTab((prev)=>({
            ...prev,autonomie:tab.autonomie
        }))
    }
    
    let id = 0;
    function ajouter_parcours()
    {
      if(tab.autonomie > distance)
        {tab.parcours.push({id:id, distance:distance});
        setTab((prev)=>({
            ...prev,parcours:tab.parcours
        }))
        id += 1;}
    }

    return (
        <div>
            <h1>Suivi des vélos Electriques</h1>
            reference <input type="text" value={veloRef} onChange={(event) => setVeloRef(event.target.value)} /> <br />

            Marque <input type="text" value={(tab)?tab.marque:''} />
            <button onClick={() => Rechercher()}>Rechercher</button> <br />

            Autonomie en [km] : <input type="text" value={(tab)?tab.autonomie:''} />
            <br />

            kilométrage <input type="text" value={(tab)?tab.kilometrage:''} />
            <br />

            nombre de minute <input type="text" value={min} onChange={(event) => setMin(Number(event.target.value))}  />
            <button onClick={() => charger()}>charger</button> <br />

            distance de parcours <input type="text" value={distance} onChange={(event) => setDistance(Number(event.target.value))}  />
            <button onClick={() => ajouter_parcours()}>ajouter parcours</button>
            <br />

            <table>
                <tr>
                    <th>id parcours</th>
                    <th>distance</th>
                </tr>

                {
                    (tab.parcours)?tab.parcours.map((velo) => {
                        return <tr>
                            <td> {velo.id} </td>
                            <td> {velo.distance} </td>
                        </tr> 
                    }):''
                }

            </table>
        </div>
    );
}

export default App;


