import pays from "../../Datas";
const initialreducer = (state={py:pays},action)=>{
       switch(action.type){
        case 'AjouterActionPAY':
            return {...state,py:[...state.py,action.payload]};

        case 'ModifierAction_PUPLATION':
            const pay=state.py.find((p)=>p.code=== parseInt(action.payload.code))
            if(pay){
                if(pay=="" || pay.SurfaceArea!=="" || pay.Population!==""){
                pay.name=action.payload.name
                pay.SurfaceArea=action.payload.SurfaceArea
                pay.Population=action.payload.Population
            }else{
                alert('no')
            }
            }
            return state
        case 'SUPREMIER_PUPLATION':
            return {...state,py:[...state.py.filter((f)=>f.code!==parseInt(action.payload))]};
        default :
            return state;
    }
}


