import React,{useState} from 'react'

export default function App() {
  const trains=[
    {id:'t1',nom:'Buraq',villes:[]},
    {id:'t2',nom:'Gazel', villes:[]},
    {id:'t3',nom:'Eagle-Wins', villes:[]},
    {id:'t4',nom:'Fennec', villes:[]},
    ]
  
    const [id,setId]=useState()
    const [train,setTrain]=useState()
    const [ville,setVille]=useState('')

    function rechercher(){
      setTrain(trains.find((t)=>t.id==id))
    }
  return (
    <div>
      <div>Gestion des trains</div>
            <div>Id: <input type="text" onChange={(e)=>setId(e.target.value)}/>
            <button onClick={rechercher}>rechercher</button>
            </div>
            <div>Nom du train: {train?train.nom:''} </div>
            
            <br></br>
    </div>
  )
}
