import React from 'react'
import {GameDataContext} from '../App';
import { Link } from 'react-router-dom';
function Startgame() {
  const { gameData } = React.useContext(GameDataContext);
  return (
    <div className='containter'>
          <div className="card mt-1">
            <div className="d-flex justify-content-between align-items-center p-2">
              <h4 className="text-green">Start game</h4>
              <Link to="/choose">Previous page</Link>
            </div>
          </div>

          <div className='card col-3 mt-3'>
            <p> <span>The maximum number chosen is : </span><b>{gameData.number}</b></p>
            <div> <span>The operation chosen is : </span><b>{gameData.operation}</b></div>
          </div>
    </div>
  )
}
export default Startgame
