import React, { useContext } from 'react';
import{Theme} from'../App'
function Pa1() {
  var clr=useContext(Theme);
  
  let styl={}
    if(clr == "dark"){
      styl={
        background :'blue',
        color:'white',
        textAlign:'center'
    }
    }else{
      styl={
          background :'red',
          color:'white',
          textAlign: 'center'
      }
    }
  return (
    <div style={styl}>
      <h1>testTheme2</h1>
    </div>
  )
}
export default Pa1
