import React from 'react'
import { useState } from 'react'

export default function App() {
  const[loisir,setLoisir]=useState([])

  function check(e){
    if(!loisir.includes(e.target.value)){
      setLoisir([...e.target.value])
    }
  }
  return (
    <div>
        <div><b>Loisirs</b></div>
        <input type='checkbox' onClick={(e)=>check(e)}  value="Sport" name='Loisirs'/> <label>Sport</label> 
        <input type='checkbox' onClick={(e)=>check(e)}  value="Lecteur" name='Loisirs'/> <label>Lecteur</label> 
        <input type='checkbox' onClick={(e)=>check(e)}  value="Musique" name='Loisirs'/> <label>Musique</label> 

        <div>
          <b>votre Loisirs :</b>
          <ul>
            {loisir&&loisir.map((l)=>{
              return <li>{l}</li>
            })}
          </ul>
        </div>
      </div>
    
  )
}
