import React, { useState } from 'react';
import plantList from '../datas/plantList'
import imgSun from "../assets/sun.svg"
import imgWater from "../assets/water.svg"
import Comand from "./Comand"

export default function Pannier(props) {
 function afch(){
  props.bntAjt(1)
 }

//pour afficher le water
var tbwater=[]
const water=(par)=>{
  tbwater=[]
 for(let i=0;i<par;i++){
  tbwater.push(<img src={imgWater}/>)
    }  
    return tbwater
  }

  //pour afficher le sun
var tbsun=[]
const sun=(par)=>{
  tbsun=[]
  for(let i=0;i<par;i++){
    tbsun.push(<img src={imgSun}/>)
  }
  return tbsun
  
}

//ajouterPanner
let pan=[]
const [afchPan,setAfchPan]=useState(pan)
function remplirPanner(){
  let imgCvr=document.getElementById('imgCvr').src;
  let titlePanier=document.getElementById('titlePanier').value
  let prix=document.getElementById('prix').value
const newlist={
  id:afchPan.length+1,
  imgCvr,
  titlePanier,
  prix
}
}

 
  return (
    <div className='card shadow-lg p-3'>
      <div className='d-flex justify-content-center gap-3'>
         <select >
          {plantList.map((options,index)=>{
            return <option key={index+1} value={options.category}>{options.category}</option>
          })}
        </select><button className="btn btn-success">Search</button>
      </div>

      <div id='perntPan' className='d-flex gap-3'>
      <Comand className="" id='comand'/>
      <div className='row mt-3'>
      {plantList.map((recp)=>{
            return  <div className='col-4'>
                      <div className="card">
                     <img src={recp.cover} className="card-img-top" id='imgCvr' alt="image1"/>
                     <div className="card-body">
                       <h5 className="card-title" id='titlePanier'>{recp.name}</h5>
                       <p className="card-text">
                        <div>{water(recp.water)}</div>
                        <div>{sun(recp.light)}</div>
                        </p>
                        <div className='d-flex justify-content-between'>
                        <button href="#" className="btn btn-success" id='btnAjt' onClick={afch}>ajouter </button>
                        <span id='prix' className='text-success'>{recp.price} $</span>
                        </div>
                     </div>
                      </div>
              </div>
          })}
        </div> 
      </div>
    </div>
  )
}
