import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { DarkAction } from './Redux/actions/InitialAction';
import { LightAction } from './Redux/actions/InitialAction';
export default function Theme() {
    let themVar=useSelector((data)=>data.color)
    const dispatch = useDispatch; 
    // const [bg,setbg]=useState(themVar)
    document.body.style=`background-color:${themVar};`
  return (
    <div className='d-flex gap-3'>
        <button onClick={()=>dispatch(DarkAction())}>Dark</button>
        <button onClick={()=>dispatch(DarkAction())}>light</button>
    </div>
  )
}
