const counterReducer = (s = 1, action) => { 
 switch (action.type) { 
 case "INCREMENT": 
 return s+1; 
 case "DECREMENT": 
 return s-1; 
 case "RESET": 
 return (s = 0); 
 default: 
 return s; 
 } 
 }; 
 export default counterReducer;
