import { useSelector, useDispatch } from "react-redux"; 
import { 
 decrement, 
 increment, 
 reset, 
} from "../Redux/actions/reduxActions"; 
export default function Counter() { 
 const counter = useSelector((state) => state); 
 const dispatch = useDispatch(); 
 return ( 
 <div style={{textAlign:"center"}}>
 <h1>
 Hello Front End Developers <br /> Here is a Simple Redux 
Project ! 
 </h1>
 <h2>Counter</h2>
 <h2>{counter}</h2>
 <button style={{margin:"20px", fontSize:"20px", width:"10%"}}
onClick={() => dispatch(increment())}>Increase</button>
 <button style={{margin:"20px", fontSize:"20px", width:"10%"}}
onClick={() => dispatch(reset())}>Reset</button>
 <button style={{margin:"20px", fontSize:"20px", width:"10%"}}
onClick={() => dispatch(decrement())}>Decrease</button>
 </div>
 ); 
}
